import { Component, OnInit } from '@angular/core';

export class HomePage implements OnInit {
  favoriteAnimes: string[] = [];

  constructor() { }

  ngOnInit() { }

  goToMainPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr';
    }
  }

  goToReleasedPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr/planning/';
    }
  }

  addToFavorites() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      const doc = iframe.contentDocument || iframe.contentWindow?.document;
      const title = doc?.querySelector('h3')?.textContent;
      if (title) {
        this.favoriteAnimes.push(title);
      }
    }
  }

  displayFavorites() {
    console.log(this.favoriteAnimes);
  }

  isAnimeFavorite(title: string) {
    return this.favoriteAnimes.includes(title);
  }
}
