import { Component, OnInit} from '@angular/core';
import {
  ActionPerformed,
  PushNotificationSchema,
  PushNotifications,
  Token,
} from '@capacitor/push-notifications';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})

export class HomePage implements OnInit {

  animeName: string = '';

  ngOnInit() {
    PushNotifications.requestPermissions().then(result => {
      if (result.receive === 'granted') {
        PushNotifications.register();
      } else {
        console.error('Permission not granted');
      }
    });

    console.log('Initializing HomePage');

    PushNotifications.addListener('registration', (token: Token) => {
      console.log('Push registration success, token: ' + token.value);
    });

    PushNotifications.addListener('registrationError', (error: any) => {
      alert('Error on registration: ' + JSON.stringify(error));
    });

    PushNotifications.addListener(
      'pushNotificationReceived',
      (notification: PushNotificationSchema) => {
        alert('Push received: ' + JSON.stringify(notification));
      },
    );

    PushNotifications.addListener(
      'pushNotificationActionPerformed',
      (notification: ActionPerformed) => {
        alert('Push action performed: ' + JSON.stringify(notification));
      },
    );
  }

  createNotificationChannel = async (animeName: string) => {
  // Create a notification channel for the anime.
    const channel = {
      id: animeName,
      name: animeName,
      description: `Notifications for the anime ${animeName}.`,
    };
    await PushNotifications.createChannel(channel);
  };

  goToMainPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr';
    }
  }

  goToReleasedPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr/planning/';
    }
  }


  addToNotification() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      const iframeSrc = iframe.src;
      if (iframeSrc) {
        console.log(iframeSrc)
        const animeName = iframeSrc.split('/')[3];

        console.log(animeName)
        this.createNotificationChannel(animeName);
        PushNotifications.register();
        alert(`You have been subscribed to notifications for the anime ${animeName}. You will receive notifications when new episodes are released.`);
    }
  }
}
}
