import { Component } from '@angular/core';
import { Platform } from '@ionic/angular';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  goBack() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe && iframe.contentWindow && iframe.contentWindow.history.length > 1) {
      iframe.contentWindow.history.back();
    } else {
      (navigator as any).app.exitApp();
    }
  }

  goToMainPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr';
    }
  }

  addToFavorites() {
    // Implement the logic to add an anime to favorites and display it in a dynamic list.
    // You can use Angular data binding and storage mechanisms to manage the list of favorites.
  }
}

<ion-content [fullscreen]="true">
  <ion-fab vertical="bottom" horizontal="end">
    <ion-fab-button (click)="goBack()">
      <ion-icon name="arrow-back"></ion-icon>
    </ion-fab-button>
  </ion-fab>
  <ion-fab vertical="bottom" horizontal="end">
    <ion-fab-button (click)="goToMainPage()">
      <ion-icon name="home"></ion-icon>
    </ion-fab-button>
  </ion-fab>
  <ion-fab vertical="bottom" horizontal="end">
    <ion-fab-button (click)="addToFavorites()">
      <ion-icon name="heart"></ion-icon>
    </ion-fab-button>
  </ion-fab>

  <div style="width: 104%; height: 100%; overflow: hidden;">
    <iframe id="myIframe" src="https://anime-sama.fr/" frameborder="0" allowfullscreen></iframe>
  </div>
</ion-content>
