import { Component, OnInit } from '@angular/core';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  favoritesList: string[] = [];
  favoritesSubject$: Subject<string[]> = new Subject<string[]>();

  goToMainPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr';
    }
  }

  goToReleasedPage() {
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    if (iframe) {
      iframe.src = 'https://anime-sama.fr/planning/';
    }
  }

  addToFavorites(event: Event) {
    // Prevent the default behavior of the button
    event.preventDefault();

    // Get the anime title
    const iframe = document.getElementById('myIframe') as HTMLIFrameElement;
    const h3Element = iframe.contentDocument?.querySelector('h3');
    const animeTitle = h3Element?.textContent;

    // Add the anime title to the favorites list
    if (animeTitle) {
      this.favoritesList.push(animeTitle);

      // Notify subscribers of the updated favorites list
      this.favoritesSubject$.next(this.favoritesList);
    }
  }
}

<ion-fab vertical="bottom" horizontal="end" slot="fixed">
  <ion-fab-button color="primary" (click)="goToReleasedPage()">
    <ion-icon name="calendar-number-outline"></ion-icon>
  </ion-fab-button>

  <ion-fab-button color="secondary" (click)="goToMainPage()">
    <ion-icon name="home"></ion-icon>
  </ion-fab-button>

  <ion-fab-button color="danger" (doubleClick)="addToFavorites($event)">
    <ion-icon name="heart"></ion-icon>
  </ion-fab-button>
</ion-fab>

<div style="width: 104%; height: 100%; overflow: hidden;">
  <iframe id="myIframe" src="https://anime-sama.fr/" frameborder="0" allowfullscreen></iframe>
</div>

<ion-list>
  <ion-item *ngFor="let anime of favoritesList">
    {{ anime }}
  </ion-item>
</ion-list>
